mapper.select:


SELECT RN, BRD_NUMBER, BRD_TITLE, BRD_CONTENT, MEM_ID, BRD_DATE
FROM (
    SELECT  /*+ INDEX_DESC(BOARD_TBL PK_BRD_NUMBER)*/ ROWNUM RN, BRD_NUMBER, BRD_TITLE, BRD_CONTENT, MEM_ID, BRD_DATE FROM BOARD_TBL
    WHERE  





         ROWNUM <= #{pageNum} * #{amount}
)
WHERE 
	RN > (#{pageNum}-1) * #{amount}

mapper.update:

UPDATE
	BOARD_TBL
SET
	BRD_TITLE = #{brd_title}, BRD_CONTENT = #{brd_content}
WHERE
	BRD_NUMBER = #{brd_number}
