mapper.select:

SELECT 
	RN, PDT_NUMBER, CTG_CODE, CTG_CODE_PRT, PDT_NAME, PDT_PRICE, PDT_DISCOUNT, PDT_COMPANY, PDT_IMG_FOLDER, PDT_IMG, PDT_DETAIL, PDT_SELL_AMOUNT, PDT_BUY_ACCEPT, PDT_SIGN_DATE, PDT_UPDATE_DATE
FROM (
	SELECT  /*+ INDEX_DESC(PRODUCT_TBL PK_PDT_NUMBER)*/ ROWNUM RN, PDT_NUMBER, CTG_CODE, CTG_CODE_PRT, PDT_NAME, PDT_PRICE, PDT_DISCOUNT, PDT_COMPANY, PDT_IMG_FOLDER, PDT_IMG, PDT_DETAIL, PDT_SELL_AMOUNT, PDT_BUY_ACCEPT, PDT_SIGN_DATE, PDT_UPDATE_DATE FROM PRODUCT_TBL
WHERE  





         ROWNUM <= #{pageNum} * #{amount}
)
WHERE 
	RN > (#{pageNum}-1) * #{amount}
